<script>
import Modal from './Modal.vue'
import SettingsGeneral from './SettingsGeneral.vue';
import SettingsPersonalization from './SettingsPersonalization.vue';
import SettingsSecurity from './SettingsSecurity.vue';

export default {
    name: 'SettingsModal',
    components: { Modal, SettingsGeneral, SettingsPersonalization, SettingsSecurity },
    computed: {
        isModalOpen() {
            return !!this.$route.query.m;
        },
        pageName() {
            return this.$route.query.page
        },
        modalContent() {
            switch (this.pageName) {
                case 'general':
                    return 'SettingsGeneral';
                case 'personalization':
                    return 'SettingsPersonalization';
                case 'security':
                    return 'SettingsSecurity';
                default:
                    return 'SettingsGeneral';
            }
        }
    },
    methods: {
        handleRoute(param) {
            this.$router.push({query: { m: 'settings', page: param }})
        }
    }
}
</script>

